import React, { useEffect, useState } from 'react';

function Animate() {
  const [value1, setValue1] = useState(250);
  useEffect(() => {
    const handleScroll = () => {
      const heroimgbg = document.querySelector('.heroimgbg');
      const expHead = document.querySelector('.exp-head');
      const text1 = document.querySelector('.exp-text-one');
      const text2 = document.querySelector('.exp-text-two');
      const expSec = document.querySelector('.expSec');
      const value = window.scrollY;

      if (value <= 900) {
        const newValue1 = 1450 - value;
        text1.style.opacity = '0';
        text2.style.opacity = '0';
        expHead.style.opacity = '0';
        setValue1(newValue1);
      }
      else {
        setValue1(250);
        text1.style.opacity = '1';
        text2.style.opacity = '1';
        expHead.style.opacity = '1';
      }
      heroimgbg.style.clipPath = `circle(${value1}px at center)`;
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [value1]);
  return (
    <section className="section bg-[#354033]">
      
      <div className="container mx-auto">
        <div className='min-h-screen'></div>
        <div className="flex justify-between column-area relative">
          <div className='content'>
            <h2 className=' text-white exp-head fixed top-[25%] opacity-0 transition-[opacity] delay-100'>Ecoworld</h2>
            <p className=' text-white exp-text-one fixed top-[30%] opacity-0 transition-[opacity] ease-in-out delay-100'>
                  A sprawling 54-acre 'Grade-A business park, <br/>Ecoworld is set within 7 million sq. ft.
            </p>
          </div>
          <div className='content self-center'>
            <p className=' text-white ms-auto exp-text-two fixed top-[50%] right-[10%] opacity-0 transition-[opacity] ease-in-out delay-100'>
                  A sprawling 54-acre 'Grade-A business park,<br/> Ecoworld is set within 7 million sq. ft.
            </p>
          </div>
        </div>
      </div>
    </section>     
  )
}

export default Animate
