import React from 'react';
import Header from './header';

const Banner = () => {
    let screenWidth = window.innerWidth;
    screenWidth = screenWidth;
    window.addEventListener('scroll', function() {
        // var imgbg = document.querySelector('.heroimgbg');
        var section = document.querySelector('.section');
        const text1 = document.querySelector('.exp-text-one');
        const text2 = document.querySelector('.exp-text-two');
        text1.style.opacity = '0';
        text2.style.opacity = '0';
        var sectionHeight = section.offsetTop;
        var circleEnd, circleStart;
        if(screenWidth>=1920){
          circleEnd = 920;
          circleStart = 1150;
        }
        else if((screenWidth<1920) && (screenWidth>=1680)){
          circleEnd = 800;
          circleStart = 1000;
        }
        else if((screenWidth<1680) && (screenWidth>=1440)){
          circleEnd = 620;
          circleStart = 850;
        }
        else if((screenWidth<1440) && (screenWidth>=1280)){
          circleEnd = 600;
          circleStart = 800;
        }
        else if((screenWidth<1280) && (screenWidth>=1024)){
          circleEnd = 600;
          circleStart = 850;
        }
        else if((screenWidth<1024) && (screenWidth>=768)){
          circleEnd = 650;
          circleStart = 820;
        }
        else if((screenWidth<768) && (screenWidth>=540)){
          circleEnd = 730;
          circleStart = 820;
        }
        else if((screenWidth<540) && (screenWidth>321)){
          circleEnd = 730;
          circleStart = 820;
        }
        else{
          circleEnd = 630;
          circleStart = 700;
        }
        var scrollPos = window.scrollY;
        var circle = circleStart - scrollPos;
        console.log(scrollPos, sectionHeight)
        if (scrollPos <= circleEnd) {
          imgbg.style.clipPath = `circle(${circle}px at center)`;
          text1.style.opacity = '0';
          text1.style.transition = '0.2s';
          text2.style.opacity = '0';
          text2.style.transition = '5s';
        }
        else{
          text1.style.opacity = '1';
          text2.style.opacity = '1';
        }
      })
    return (
        <section className="section bg-[#354033] mt-[-2px]">
          <div className="container mx-auto w-[90%] 3xl:w-[39%] 2xl:w-[46%] xl:w-[50%] md:w-[88%]">
            <div className="flex justify-between place-items-center column-area">
              <div className='content'>
                {/* <h2 className=' text-white exp-head fixed top-[25%] opacity-0 transition-[opacity] delay-100'>Ecoworld</h2> */}
                <p className=' text-white text-xs md:text-sm text-center md:text-left exp-text-one fixed top-[25%] 2xl:top-[35%] xl:top-[30%] md:top-[40%] opacity-0 transition-[opacity] ease-in-out delay-100 w-[90%] 2xl:w-[18%] xl:w-[23%] lg:w-[32%] md:w-[42%]'>
                Creating a world-class workplace experience, that continues to inspire you.
                </p>
              </div>
              <div className='content self-center'>
                <p className=' text-white text-xs md:text-nm text-center md:text-left ms-auto exp-text-two fixed top-[68%] xl:top-[50%] md:top-[55%] right-[2%] 2xl:right-[19%] xl:right-[9%]  md:right-[2%] opacity-0 transition-[opacity] ease-in-out delay-100 2xl:w-[18%] w-[90%] xl:w-[23%] lg:w-[32%] md:w-[42%]'>
                     100% recycling and resume of water 85% occuupied spaces have views of the outdoors.
                </p>
              </div>
            </div>
            <div className='min-h-screen'></div>
            <div className='block md:hidden min-h-screen'></div>
            <div className='block md:hidden min-h-screen'></div>
          </div>
        </section>     
      )
};

export default Banner;
