import React from 'react';
import Header from '../header';

const Banner = () => {
    window.addEventListener('scroll', function() {
        
        const bannerBg = document.querySelector('.banner-bg');
        const oppText = document.querySelector('.oppTextOne');
        var wS = window.scrollY;
        var circle = 1500 - wS;
        bannerBg.style.clipPath = `circle(${circle}px at center)`;
        var hT = bannerBg.offsetHeight;
        console.log(wS, hT);
        if(wS>hT){
            oppText.style.opacity = 1;
        }
        else{
            oppText.style.opacity = 0;
        }
    })
    return (
        <div className="bg-[#e6e7e8] z-50">
            <Header/>
            <div className="min-h-screen grid place-content-center banner-bg"></div>
            <div className='mx-auto w-[90%] 3xl:w-[39%] 2xl:w-[46%] xl:w-[59%] md:w-[88%]'>
                <div className='flex justify-between'>
                    <div className='content'>
                        <h2 className='text-5xl font-[500] text-darkGreen fixed top-[25%] xl:top-[30%] md:top-[40%] opacity-0 transition-[opacity] delay-100 oppText'>Connectivity</h2>
                    </div>
                    <div className='content'>
                        <p className='text-black text-md md:text-xl text-center md:text-left ms-auto exp-text-two fixed top-[68%] xl:top-[50%] md:top-[55%] right-[2%] 2xl:right-[19%] xl:right-[9%]  md:right-[2%] opacity-0 transition-[opacity] ease-in-out delay-100 2xl:w-[17%] w-[90%] xl:w-[23%] lg:w-[32%] md:w-[42%] oppText'>
                        100% recycling and resume of water 85% occuupied spaces have views of the outdoors.
                        </p>
                    </div>
                </div>
            </div>
            <div className='min-h-screen'></div>
            <div className='min-h-screen'></div>
            <div className='min-h-screen endFirst'></div>
        </div>
    );
};

export default Banner;
