import { useState } from 'react'
import { Dialog } from '@headlessui/react'
import { Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline'

const navigation = [
  { name: 'Product', href: '#' },
  { name: 'Features', href: '#' },
  { name: 'Marketplace', href: '#' },
  { name: 'Company', href: '#' },
]

export default function Hero() {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false)

  window.addEventListener('scroll', function() {
    // var contain = document.querySelector('.contain');
    var imgbg = document.querySelector('.heroimgbg');
    var heading = document.querySelector('.heading');
    const section = document.querySelector('.section');
    var text = document.querySelector('.text');
    var scrollPos = window.scrollY;
    let secPos = section.offsetTop;
    var a = secPos-100;
    console.log(a)
    console.log(scrollPos);
    if(scrollPos==0){
      var scale = 1;
      heading.style.opacity = 1;
      text.style.display = 'block';
      
    }
    else{
      var scale = scrollPos/1;
      text.style.display = 'none';
      heading.style.opacity = 0.9;
      
    }
    heading.style.scale = scale;

    if(scrollPos>=secPos){
      heading.style.display = 'none';
      imgbg.style.opacity = 1;
      heading.style.opacity = 0.5;
    }
    else{
      heading.style.display = 'block';
      imgbg.style.opacity = 0;
    }
    
   
    // heading.style.opacity = 0.9;
    // imgbg.style.opacity = 1;
    
    // text.style.opacity = 0;
    
    // text.style.scale = scale;
    // console.log(scrollPos);
    // if(scrollPosCopy>550){ 
    //   heading.style.display = 'none';
    // }
    // else{
    //   heading.style.display = 'block';
    // }
    // if(scrollPosCopy<20){
    //   heading.style.scale = 1;
    //   heading.style.opacity = 1;
    //   imgbg.style.opacity = 0;
    //   text.style.opacity = 1;
    //   text.style.display = 'block';
    //   text.style.scale = 1;
    // }
    // else{
    //   heading.style.opacity = 0.9;
    //   imgbg.style.opacity = 1;
    //   heading.style.scale = scale;
    //   // text.style.opacity = 0;
    //   text.style.display = 'none';
    //   text.style.scale = scale;
    // } 
    // if(scrollPosCopy >=300){
    //   // console.log('hello');
    //   imgbg.style.opacity = 1;
    //   heading.style.opacity = 0;
    // }
  })
  return (
    <div className="bg-[#354033] z-50">
      <header className="fixed inset-x-0 top-0 z-50">
        <nav className="flex items-center justify-between p-6 lg:px-8" aria-label="Global">
          <div className="flex lg:flex-1">
            <a href="#" className="-m-1.5 p-1.5">
              <span className="sr-only">Eco World</span>
              <span className='text-white uppercase'>Eco World</span>
              {/* <img
                className="h-8 w-auto"
                src="https://tailwindui.com/img/logos/mark.svg?color=indigo&shade=600"
                alt=""
              /> */}
            </a>
          </div>
          <div className="flex">
            <button
              type="button"
              className="-m-2.5 inline-flex items-center justify-center rounded-md p-2.5 text-gray-700"
              onClick={() => setMobileMenuOpen(true)}
            >
              <span className="sr-only">Open main menu</span>
              <Bars3Icon className="h-6 w-6 text-white" aria-hidden="true" />
            </button>
          </div>
        </nav>
        <Dialog as="div" className="" open={mobileMenuOpen} onClose={setMobileMenuOpen}>
          <div className="fixed inset-0 z-50" />
          <Dialog.Panel className="fixed inset-y-0 right-0 z-50 w-full overflow-y-auto bg-white px-6 py-6 sm:max-w-sm sm:ring-1 sm:ring-gray-900/10">
            <div className="flex items-center justify-between">
              <a href="#" className="-m-1.5 p-1.5">
                <span className="sr-only">Your Company</span>
                <img
                  className="h-8 w-auto"
                  src="https://tailwindui.com/img/logos/mark.svg?color=indigo&shade=600"
                  alt=""
                />
              </a>
              <button
                type="button"
                className="-m-2.5 rounded-md p-2.5 text-gray-700"
                onClick={() => setMobileMenuOpen(false)}
              >
                <span className="sr-only">Close menu</span>
                <XMarkIcon className="h-6 w-6" aria-hidden="true" />
              </button>
            </div>
            <div className="mt-6 flow-root">
              <div className="-my-6 divide-y divide-gray-500/10">
                <div className="space-y-2 py-6">
                  {navigation.map((item) => (
                    <a
                      key={item.name}
                      href={item.href}
                      className="-mx-3 block rounded-lg px-3 py-2 text-base font-semibold leading-7 text-gray-900 hover:bg-gray-50"
                    >
                      {item.name}
                    </a>
                  ))}
                </div>
              </div>
            </div>
          </Dialog.Panel>
        </Dialog>
      </header>

      <div className="relative isolate px-6 pt-14 lg:px-8 contain">
        <div className="mx-auto max-w-2xl py-32 sm:py-48 lg:py-56 ">
          <div className="text-center heading bg-[#354033] scale-100">
            <h1 className="text-4xl font-bold tracking-tight text-white sm:text-6xl uppercase" data-aos="fade-up"
                    data-aos-easing="ease-in-back" data-aos-delay="0" data-aos-duration="1000">
              EcoWorld
            </h1>
            <p className="mt-6 text-lg leading-8 text-white text" data-aos="fade-up"
                    data-aos-easing="ease-in-back" data-aos-delay="0" data-aos-duration="2000">
              Ideas don't come out of nowhere, they come from a place. <br/><span> A place called inspiration.</span> 
            </p>
            </div>
            <div className='text-center'>
            <div className="mt-10 flex items-center justify-center gap-x-6">
              <a href="#" className="text-sm font-semibold leading-6 text-[#436d3d]">
                Keep Scrolling <span aria-hidden="true">â†’</span>
              </a>
            </div>
          </div>
        </div>
        {/* <div className="heroimgbg"></div> */}
      </div>
    </div>
  )
}
