  //     // else{
  //     //   imgbg.style.display = 'block';
  //     // }
      
  // })
  var myVariable = 0;
  var incrementAmount = 1;
  var lastScrollPosition = 0;
  window.addEventListener('scroll', function() {
    const imgbg = document.querySelector('.imgbg');
    var heroimgbg = document.querySelector('.heroimgbg');
    const section = document.querySelector('.section');
    const expHead = document.querySelector('.exp-head');
    const text1 = document.querySelector('.exp-text-one');
    const text2 = document.querySelector('.exp-text-two');
    const demo = document.querySelector('.demo');
    let demoPos = demo.offsetTop;
      var value = window.scrollY;
      if(value<=900){
        var value1 = 1450 - value
        // console.log(value1);
      }
      else{
        value1 = 300;
      }

      if((value>=demoPos-800)){
        text1.style.opacity = '1';
        text2.style.opacity = '1';
        expHead.style.opacity = '1';
      }
      else{
        text1.style.opacity = '0';
        text2.style.opacity = '0';
        expHead.style.opacity = '0';
      }
      
      heroimgbg.style.clipPath = "circle(" + value1 + "px at center)";
  })
  return (
    <section className="section bg-[#354033]">
      
      <div className="container mx-auto">
        {/* <div className='min-h-screen'></div> */}
        {/* <div className='min-h-screen'></div> */}
