import React from 'react'

function Animate() {
  window.addEventListener('scroll', function() {
    const imgbg = document.querySelector('.imgbg');
    var heroimgbg = document.querySelector('.heroimgbg');
    const section = document.querySelector('.section');
    var demo = document.querySelector('.demo');
      var value = window.scrollY;
      let secPos = section.offsetTop;
      // console.log(pos);
      if(value<=secPos){
        var value1 = 1200 - value
        // console.log(value1);
      }
      else{
        value1 = 300;
      }
      
      heroimgbg.style.clipPath = "circle(" + value1 + "px at center)";
  })
  return (
    <section className="section">
      
      <div className="container mx-auto">
        <div className='min-h-screen'></div>
        <div className="grid grid-cols-2 gap-[640px] column-area">
          <div className='content'>
            <h2 data-aos="fade-up" data-aos-easing="ease-in-back" data-aos-delay="0" data-aos-duration="1000" className=' text-white'>Ecoworld</h2>
            <p data-aos="fade-up" data-aos-easing="ease-in-back" data-aos-delay="0" data-aos-duration="2000" className=' text-white'>
                  A sprawling 54-acre 'Grade-A business park, Ecoworld is set within 7 million sq. ft.
            </p>
          </div>
          <div className='content self-center'>
            <p data-aos="fade-up" data-aos-easing="ease-in-back" data-aos-delay="0" data-aos-duration="2500" className=' text-white'>
                  A sprawling 54-acre 'Grade-A business park, Ecoworld is set within 7 million sq. ft.
            </p>
          </div>
        </div>
      </div>
    </section>     
  )
}

export default Animate
