import React from 'react'

function Experiance() {
  window.addEventListener('scroll', function() { 
    const expSec = document.querySelector('.expSec');
    const expbg = document.querySelector('.expbg');
    const heroimgbg = document.querySelector('.heroimgbg');
    const text1 = document.querySelector('.content');
    const connSection = document.querySelector('.connSlider');
    const text2 = document.querySelector('.exp-text-two');
    var hT = expSec.offsetTop;
    // var hH = expSec.offsetHeight;
    // var wH = window.innerHeight || document.documentElement.clientHeight;
    var wS = window.scrollY;
    var conBot = 0;
    var conTop = 100;
    if(wS>= hT){
      heroimgbg.style.display = 'none';
      text1.style.display = 'none';
      text2.style.display = 'none';
      connSection.classList.remove('hidden');
      // connSection.classList.add('expSlider');
      expbg.style.clipPath = `circle(${conBot}% at center 35%)`;
    }
    else{
      heroimgbg.style.display = 'block';
      text1.style.display = 'block';
      text2.style.display = 'block';
      // connSection.classList.remove('expSlider');
      // connSection.classList.remove('hidden');
      connSection.classList.add('hidden');
      expbg.style.clipPath = `circle(${conTop}% at center)`;
    }
    // if(wS >= hT){
    //   connSection.classList.remove('hidden');
    //   // connSection.classList.add('hidden');
    // }
    // else{
    //   connSection.classList.add('hidden');
    // }
  })

  return ( 
    <section className='expSec bg-[#354033] relative'>
      <div className='expbg'>
        <div className='min-h-screen md:pb-[180px] grid place-content-end expContent text-white text-center'>
            <h2 className='text-6xl font-[500]'>Experience Ecoworld</h2>
            <p className='font-[400] w-10/12 lg:w-7/12 mx-auto mt-7 text-lg'>
            Our placemaking capabilities integrate in a dynamic workplace environment with green open spaces
            and a native landscape to inspire innovation and creativity. The campus seamlessly blends into a
            live-work-play ecosystem with diverse options for connection, collaboration and post-work unwinding.
            </p>
            
        </div>
       
      </div>
      {/* <div className='min-h-screen'></div> */}
    </section>
  )
}

export default Experiance
