  window.addEventListener('scroll', function() {
    const imgbg = document.querySelector('.imgbg');
    var heroimgbg = document.querySelector('.heroimgbg');
    const section = document.querySelector('.section');
      var value = window.scrollY;
      var secOffset = section.offsetTop;
      // const sticky = section.offsetTop;
      // console.log(value);
      var value1 = 1000 - 500
      // console.log(value);
      if(value>=secOffset){ 
        var val = window.scrollY;
        val = 100;
        console.log(val);
        let s = (section.offsetTop - window.scrollY)/section.clientHeight;
        console.log(s);
        heroimgbg.style.clipPath = "circle(" + 100 + "px at center)";
        // imgbg.style.opacity = 1;
        // heroimgbg.style.position = 'relative';
      }
      else{
        heroimgbg.style.clipPath = "circle(" + 1000 + "px at center)";
      }
      // else{
        // imgbg.style.clipPath = "circle(" + value + "px at center)";
      // }
      
      // console.log("circle(" + value1 + "px at center)");
  })
  return (
    <section className="section">
      <div className="imgbg"></div>
      <div className="content">
          <h2>Heading Two</h2>
          <p>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Repellendus, voluptas nostrum? Commodi mollitia suscipit explicabo perspiciatis
          </p>
      </div>
    </section>     
  )
}

export default Animate
