import React from 'react';
import Header from '../header';

const Banner = () => {
    window.addEventListener('scroll', function() {
        var wS = 1500 - window.scrollY;
        const bannerBg = document.querySelector('.banner-bg');
        bannerBg.style.clipPath = `circle(${wS}px at center)`;
    })
    return (
        <div className="bg-[#e6e7e8] z-50">
            <Header/>
            <div className="min-h-screen grid place-content-center banner-bg"></div>
            <div className='container'>
                <div className='flex justify-between'>
                    <div className='content'>
                        <h2 className='text-6xl text-darkGreen fixed top-[25%]  transition-[opacity] delay-100'>Connectivity</h2>
                    </div>
                    <div className='content'>
                        <p className=' text-black text-md md:text-xl text-center md:text-left ms-auto exp-text-two fixed top-[68%] xl:top-[50%] md:top-[55%] right-[2%] 2xl:right-[19%] xl:right-[9%]  md:right-[2%] transition-[opacity] ease-in-out delay-100 2xl:w-[17%] w-[90%] xl:w-[23%] lg:w-[32%] md:w-[42%]'>
                        100% recycling and resume of water 85% occuupied spaces have views of the outdoors.
                        </p>
                    </div>
                </div>
            </div>
            <div className='min-h-screen'></div>
            <div className='min-h-screen'></div>
            <div className='min-h-screen'></div>
        </div>
    );
};

export default Banner;
