import React from 'react'

function Animate() {
  window.addEventListener('scroll', function() {
    const imgbg = document.querySelector('.imgbg');
    var heroimgbg = document.querySelector('.heroimgbg');
    const section = document.querySelector('.section');
    const text1 = document.querySelector('.exp-text-one');
    const text2 = document.querySelector('.exp-text-two');
    const demo = document.querySelector('.demo');
    let demoPos = demo.offsetTop;
    let secPos = section.offsetTop;
    // console.log(secPos);
      var value = window.scrollY;
      var val = value/2;
      // console.log(val);
      if((value>=secPos) && (value<=demoPos-1000)){
        var value1 = 1200 - val
        // console.log(value1);
        heroimgbg.style.clipPath = "circle(" + value1 + "px at center)";
        text1.style.position = 'fixed';
        text2.style.position = 'fixed';
      }
      else{
        value1 = 1000;
        text1.style.position = 'fixed';
        text2.style.position = 'fixed';
      }
      // let demoPos = demo.offsetTop;
    
      // if(scrollPos >= demoPos-600){
      //   console.log('hello');
      //   imgbg.style.display = 'none';
      // }
      // else{
      //   imgbg.style.display = 'block';
      // }
      
  })
  return (
    <section className="section bg-[#354033]">
      
      <div className="container mx-auto">
        <div className='min-h-screen'></div>
        <div className='min-h-screen'></div>
        {/* <div className='min-h-screen'></div> */}
        <div className="grid grid-cols-2 gap-[640px] column-area">
        {/* <div className="heroimgbg"></div> */}
          <div className='content'>
            <h2 data-aos="fade-up" data-aos-easing="ease-in-back" data-aos-delay="0" data-aos-duration="1000" className=' text-white exp-head'>Ecoworld</h2>
            <p data-aos="fade-up" data-aos-easing="ease-in-back" data-aos-delay="0" data-aos-duration="2000" className=' text-white exp-text-one'>
                  A sprawling 54-acre 'Grade-A business park, Ecoworld is set within 7 million sq. ft.
            </p>
          </div>
          <div className='content self-center'>
            <p data-aos="fade-up" data-aos-easing="ease-in-back" data-aos-delay="0" data-aos-duration="2000" className=' text-white exp-text-two'>
                  A sprawling 54-acre 'Grade-A business park, Ecoworld is set within 7 million sq. ft.
            </p>
          </div>
        </div>
      </div>
    </section>     
  )
}

export default Animate
