import React from 'react'

function Animate() {
  window.addEventListener('scroll', function() {
    var contain = document.querySelector('.contain');
    let s = (contain.offsetTop + contain.clientHeight*.5 - window.scrollY)/contain.clientHeight;
    s = (s > 1) ? 0 : 1-s;
    var maskgroup = document.querySelector('#m1 g');
    maskgroup.setAttribute('transform', `translate(1500 750) scale(${1 + s * 90})`);
  });
  return (
    <section className="section">
    <div className="container">
        <div className="row">
            <div className="col-12">
                <h1 className="title">Heading One</h1>
                <div className="img-bg"></div>
                <div className="content">
                    <h2>Heading Two</h2>
                    <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Repellendus, voluptas nostrum? Commodi mollitia suscipit explicabo perspiciatis</p>
                </div>
            </div>
        </div>
    </div>
</section>
  )
}

export default Animate
