import React, { useEffect, useState } from 'react';

function Animate() {
  const [value1, setValue1] = useState(20);
  useEffect(() => {
    const handleScroll = () => {
      const heroimgbg = document.querySelector('.heroimgbg');
      // const expHead = document.querySelector('.exp-head');
      const text1 = document.querySelector('.exp-text-one');
      const text2 = document.querySelector('.exp-text-two');
      const expSec = document.querySelector('.expSec');
      const value = window.scrollY;

      if (value <= 900) {
        const newValue1 = 1450 - value;
        text1.style.opacity = '0';
        text2.style.opacity = '0';
        // expHead.style.opacity = '0';
        setValue1(newValue1);
        heroimgbg.style.clipPath = `circle(${value1}px at center)`;
      }
      else {
        setValue1(20);
        text1.style.opacity = '1';
        text2.style.opacity = '0.6';
        // expHead.style.opacity = '1';
        heroimgbg.style.clipPath = `circle(${value1}% at center)`;
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [value1]);
  return (
    <section className="section bg-[#354033]">
       <div className='flex justify-between text-center text-white fixed z-50 w-[100%] px-5 top-[20%] connSection'>
          {/* <div className='z-50'>
            <img className='rounded-full' src="/images/hero.jpg" />
            <h2>Heading</h2>
            <p>Sub Heading</p>
          </div>
          <div>
            <img src="/images/hero.jpg" />
            <h2>Heading</h2>
            <p>Sub Heading</p>
          </div>
          <div>
            <h2>Heading</h2>
            <p>Sub Heading</p>
          </div>
          <div>
          <img src="/images/hero.jpg" />
            <h2>Heading</h2>
            <p>Sub Heading</p>
          </div>
          <div>
          <img src="/images/hero.jpg" />
            <h2>Heading</h2>
            <p>Sub Heading</p>
          </div> */}
              
        </div>
      <div className="container mx-auto md:w-[55%]">
        <div className="flex justify-between column-area">
          <div className='content'>
            {/* <h2 className=' text-white exp-head fixed top-[25%] opacity-0 transition-[opacity] delay-100'>Ecoworld</h2> */}
            <p className=' text-white text-xl exp-text-one fixed top-[30%] opacity-0 transition-[opacity] ease-in-out delay-100 lg:w-[23%] xl:w-[23%] xxl:w-[16%] '>
            Creating a world-class workplace experience, that continues to inspire you.
            </p>
          </div>
          <div className='content self-center'>
            <p className=' text-white text-xl ms-auto exp-text-two fixed top-[50%] right-[10%] opacity-0 transition-[opacity] ease-in-out delay-100 xxl:w-[16%] lg:w-[23%]'>
                 100% recycling and resume of water 85% occuupied spaces have views of the outdoors.
            </p>
          </div>
        </div>
        <div className='min-h-screen'></div>
      </div>
    </section>     
  )
}

export default Animate
