import React from 'react'

function Animate() {
  window.addEventListener('scroll', function() {
    const imgbg = document.querySelector('.imgbg');
    var heroimgbg = document.querySelector('.heroimgbg');
    const section = document.querySelector('.section');
      var value = window.scrollY;
      if(value<=900){
        var value1 = 1200 - value
        // console.log(value1);
      }
      else{
        value1 = 300;
      }
      
      heroimgbg.style.clipPath = "circle(" + value1 + "px at center)";
  })
  return (
    <section className="section">
      {/* <div className='min-h-screen'></div> */}
      <div class="columns-3 min-h-screen">
              <div className='content'>
                <p data-aos="fade-up"
                    data-aos-easing="ease-in-back" data-aos-delay="0" data-aos-duration="1500" className=' text-white'>
                      A sprawling 54-acre
                    'Grade-A business park, Ecoworld is 
                    set within 7 million sq. ft.</p>
              </div>
        </div>
      <div class="container mx-auto">
        
      </div>
    </section>     
  )
}

export default Animate
